mapper.select:
select count(id) from "project_commission"

    1 = 1


        and   designer like #{pattern}



        and  designer_assistant like #{designerAssistantpattern}


        and commission_state = #{commissionState, typeHandler=com.dms.handler.CommissionStateEnumHandler}


        and contract_id = #{contractId}


        and pay_contract_ratio >= #{payContractRatio}


        and pay_project_ratio >= #{payProjectRatio}



        and contract_date > #{contractDate}


        and first_commission_date >= #{firstCommissionStartDate}
        and first_commission_date <= #{firstCommissionEndDate}


        and actual_start_time > #{actualStartTime}


        and actual_end_time > #{actualEndTime}


        and balance_time > #{balanceTime}


        and balance_commission_date > #{balanceCommissionDate}

select * from
(
select *, row_number() over (order by id) rn from "project_commission"

    1 = 1


        and designer like #{pattern}



        and  designer_assistant like #{designerAssistantpattern}



        and ac_number like #{acNumberPattern}




        and contract_id like #{contractIdPattern}


        and pay_contract_ratio >= #{payContractRatio}


        and pay_project_ratio >= #{payProjectRatio}



        and commission_state = #{commissionState, typeHandler=com.dms.handler.CommissionStateEnumHandler}


        and contract_date > #{contractDate}


        and first_commission_date >= #{firstCommissionStartDate}
        and first_commission_date < #{firstCommissionEndDate}


        and actual_start_time > #{actualStartTime}


        and actual_end_time > #{actualEndTime}


        and balance_time > #{balanceTime}


        and balance_commission_date >= #{balanceCommissionStartDate}
        and balance_commission_date < #{balanceCommissionEndDate}


) t


        t.rn BETWEEN #{start} and #{end}




        order by designer


        order by id




        desc


        asc
